    private void decimalButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_decimalButtonActionPerformed
                
        if(resultsLBL.getText().length() > 0){
            if(!resultsLBL.getText().contains(".")){
                if(!resultsLBL.getText().endsWith(" ")){
                    resultsLBL.setText(resultsLBL.getText() + "."); 
                }
                else{
                    resultsLBL.setText(resultsLBL.getText() + "0.");
                }
            }
            else{
                if(resultsLBL.getText().contains("/")){
                    if(resultsLBL.getText().split("/").length == 2){ 
                        if(resultsLBL.getText().endsWith(" ")){
                            resultsLBL.setText(resultsLBL.getText() + "0.");
                        }
                        else if(!resultsLBL.getText().split("/")[1].contains(".")){
                            resultsLBL.setText(resultsLBL.getText() + ".");
                        }
                    }
                }
                else if(resultsLBL.getText().contains("X")){
                    if(resultsLBL.getText().split("X").length == 2){
                        if(resultsLBL.getText().endsWith(" ")){
                            resultsLBL.setText(resultsLBL.getText() + "0.");
                        }
                        else if(!resultsLBL.getText().split("X")[1].contains(".")){
                            resultsLBL.setText(resultsLBL.getText() + ".");
                        }
                    }
                }
                else if(resultsLBL.getText().contains("-")){
                    if(resultsLBL.getText().split("\\-").length == 2){
                        if(resultsLBL.getText().endsWith(" ")){
                            resultsLBL.setText(resultsLBL.getText() + "0.");
                        }
                        else if(!resultsLBL.getText().split("\\-")[1].contains(".")){
                            resultsLBL.setText(resultsLBL.getText() + ".");
                        }
                    }
                }
                else if(resultsLBL.getText().contains("+")){
                    
                    if(resultsLBL.getText().split("\\+").length == 2){
                        if(resultsLBL.getText().endsWith(" ")){
                            resultsLBL.setText(resultsLBL.getText() + "0.");
                        }
                        else if(!resultsLBL.getText().split("\\+")[1].contains(".")){
                            resultsLBL.setText(resultsLBL.getText() + ".");
                        }
                    }
                }
                
            }
        }
        else{
            resultsLBL.setText("0.");
        }
        
    }//GEN-LAST:event_decimalButtonActionPerformed

    private void divideButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_divideButtonActionPerformed
        
        if(!resultsLBL.getText().equals("") && !resultsLBL.getText().endsWith("0.")){
            if(!resultsLBL.getText().contains(" / ") && !resultsLBL.getText().contains(" X ")
                    && !resultsLBL.getText().contains(" - ") && !resultsLBL.getText().contains(" + ")){
                resultsLBL.setText(resultsLBL.getText() + " / ");
            }
            else if(resultsLBL.getText().contains(" X ")){
                resultsLBL.setText(resultsLBL.getText().replace(" X ", " / "));
            }
            else if(resultsLBL.getText().contains(" - ")){
                resultsLBL.setText(resultsLBL.getText().replace(" - ", " / "));
            }
            else if(resultsLBL.getText().contains(" + ")){
                resultsLBL.setText(resultsLBL.getText().replace(" + ", " / "));
            }
        }
        
    }//GEN-LAST:event_divideButtonActionPerformed

