    private void equalsButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_equalsButtonActionPerformed
        
        if(!(resultsLBL.getText().endsWith(" ") || resultsLBL.getText().equals(""))){
            Calculations calc = new Calculations();
            
            if(resultsLBL.getText().contains(" / ")){
                resultsLBL.setText("" + calc.calculate(Double.parseDouble(resultsLBL.getText().split(" / ")[0]), "/", Double.parseDouble(resultsLBL.getText().split(" / ")[1])));
            }
            else if(resultsLBL.getText().contains(" X ")){
                resultsLBL.setText("" + calc.calculate(Double.parseDouble(resultsLBL.getText().split(" X ")[0]), "X", Double.parseDouble(resultsLBL.getText().split(" X ")[1])));
            }
            else if(resultsLBL.getText().contains(" - ")){
                resultsLBL.setText("" + calc.calculate(Double.parseDouble(resultsLBL.getText().split(" - ")[0]), "-", Double.parseDouble(resultsLBL.getText().split(" - ")[1])));
            }
            else if(resultsLBL.getText().contains("+")){
                resultsLBL.setText("" + calc.calculate(Double.parseDouble(resultsLBL.getText().split("\\+")[0]), "+", Double.parseDouble(resultsLBL.getText().split("\\+")[1])));
            }
            
        }
        
    }//GEN-LAST:event_equalsButtonActionPerformed

